    if (type === 'none') {
      lineArrow.setAttribute('startHead', false);
    } else if (type === 'default') {
      lineArrow.setAttribute('startHead', true);
    } else if (type === 'circle') {
      lineArrow.setAttribute('startHead', new Circle({ attrs: { r: 10 } }));
    } else if (type === 'custom arrowhead') {
