import { Circle } from '@antv/g';
import { Canvas } from '@antv/g-webgl';
import Stats from 'stats.js';

// create a canvas
const canvas = new Canvas({
  container: 'container',
  width: 600,
  height: 500,
});

// create a circle
const circle = new Circle({
  attrs: {
    fill: '#1890FF',
    stroke: '#F04864',
    lineWidth: 4,
  },
});

for (let i = 0; i < 1000; i++) {
  // create an instance of circle
