    circle3.stopAnimation(true);
  },
  start: () => {
    circle1.attr({
      x: 100,
      y: 100,
      r: 20,
      fill: '#1890FF',
      stroke: '#F04864',
      lineWidth: 4,
    });
    circle1.animate(
      {
        r: 50,
      },
      {
        delay: 0,
        duration: 2000,
        easing: 'easeLinear',
        callback: () => { },
        repeat: true,
      }
    );

    circle2.attr({
      x: 100,
      y: 200,
      r: 50,
      fill: '#1890FF',
      stroke: '#F04864',
      lineWidth: 4,
    });
    circle2.animate(
      {
        fill: '#F04864',
      },
      {
        delay: 0,
        duration: 2000,
        easing: 'easeLinear',
        callback: () => { },
