moonOrbitFolder.add(moonOrbitConfig, 'show').name('show');
moonOrbitFolder.add(moonOrbitConfig, 'z-index', 0, 100).onChange((zIndex) => {
  moonOrbit.style.zIndex = zIndex;
});

