    canvas.setRenderer(renderer === 'canvas' ? canvasRenderer : renderer === 'webgl' ? webglRenderer : svgRenderer);
  });
  rendererFolder.open();
  var polygonFolder = gui.addFolder('polygon');
  var polygonConfig = {
    fill: '#fff',
    stroke: '#F04864',
    lineWidth: 4,
    fillOpacity: 1,
    strokeOpacity: 1,
    anchorX: 0,
