  polygonFolder.addColor(polygonConfig, 'fill').onChange(function (color) {
    polygon.attr('fill', color);
  });
  polygonFolder.addColor(polygonConfig, 'stroke').onChange(function (color) {
    polygon.attr('stroke', color);
  });
  polygonFolder.add(polygonConfig, 'lineWidth', 1, 20).onChange(function (lineWidth) {
    polygon.attr('lineWidth', lineWidth);
  });
  polygonFolder.add(polygonConfig, 'fillOpacity', 0, 1, 0.1).onChange(function (opacity) {
    polygon.attr('fillOpacity', opacity);
  });
  polygonFolder.add(polygonConfig, 'strokeOpacity', 0, 1, 0.1).onChange(function (opacity) {
    polygon.attr('strokeOpacity', opacity);
  });
  polygonFolder.add(polygonConfig, 'anchorX', 0, 1, 0.1).onChange(function (anchorX) {
    polygon.attr('anchor', [anchorX, polygonConfig.anchorY]);
  });
  polygonFolder.add(polygonConfig, 'anchorY', 0, 1, 0.1).onChange(function (anchorY) {
