  solarSystem.setPosition(300, 250);
  earthOrbit.translate(100, 0);
  moonOrbit.translate(100, 0);
  canvas.appendChild(solarSystem); // stats

  var stats = new _stats.default();
  stats.showPanel(0);
  var $stats = stats.dom;
  $stats.style.position = 'absolute';
  $stats.style.left = '0px';
  $stats.style.top = '0px';
  var $wrapper = document.getElementById('container');
  $wrapper.appendChild($stats);
  canvas.on('afterrender', function () {
    if (stats) {
      stats.update();
    }

    solarSystem.rotateLocal(1);
    earthOrbit.rotateLocal(2);
  }); // GUI

  var gui = new dat.GUI({
    autoPlace: false
  });
  $wrapper.appendChild(gui.domElement);
  var rendererFolder = gui.addFolder('renderer');
  var rendererConfig = {
    renderer: 'canvas'
  };
  rendererFolder.add(rendererConfig, 'renderer', ['canvas', 'webgl', 'svg']).onChange(function (renderer) {
    canvas.setRenderer(renderer === 'canvas' ? canvasRenderer : renderer === 'webgl' ? webglRenderer : svgRenderer);
  });
  rendererFolder.open();
  var cameraFolder = gui.addFolder('camera actions');
  var cameraConfig = {
    panX: 0,
    panY: 0,
    zoom: 1,
    roll: 0
  };
  var origin = camera.getPosition();
  cameraFolder.add(cameraConfig, 'panX', -300, 300).onChange(function (panX) {
    var current = camera.getPosition();
    camera.pan(origin[0] + panX - current[0], 0);
  });
  cameraFolder.add(cameraConfig, 'panY', -300, 300).onChange(function (panY) {
    var current = camera.getPosition();
    camera.pan(0, origin[1] + panY - current[1]);
  });
  cameraFolder.add(cameraConfig, 'roll', -90, 90).onChange(function (roll) {
    camera.rotate(0, 0, roll);
  });
  cameraFolder.add(cameraConfig, 'zoom', 0, 10).onChange(function (zoom) {
    camera.setZoom(zoom);
  });
  cameraFolder.open();
});

