pathArrowFolder.addColor(pathArrowConfig, 'stroke').onChange((color) => {
  pathArrow.setAttribute('stroke', color);
});
pathArrowFolder.add(pathArrowConfig, 'lineWidth', 1, 20).onChange((lineWidth) => {
