  .onChange((type) => {
    if (type === 'none') {
      pathArrow.setAttribute('startHead', false);
    } else if (type === 'default') {
      pathArrow.setAttribute('startHead', true);
    } else if (type === 'circle') {
      pathArrow.setAttribute('startHead', new Circle({ attrs: { r: 10 } }));
    } else if (type === 'image') {
      const image = new Image({
