  earthOrbitFolder.add(earthOrbitConfig, 'z-index', 0, 100).onChange(function (zIndex) {
    earthOrbit.setZIndex(zIndex);
  });
  var moonOrbitFolder = gui.addFolder('moonOrbit');
  var moonOrbitConfig = {
    visibility: true,
    'z-index': 0
  };
  moonOrbitFolder.add(moonOrbitConfig, 'visibility').onChange(function (visible) {
    if (visible) {
      moonOrbit.show();
    } else {
      moonOrbit.hide();
    }
  });
  moonOrbitFolder.add(moonOrbitConfig, 'z-index', 0, 100).onChange(function (zIndex) {
    moonOrbit.setZIndex(zIndex);
