    }
  });
  var grid = new _gPlugin3d.Grid({
    attrs: {
      width: 400,
      height: 400,
      fill: '#1890FF'
    }
  });
  group.appendChild(grid);
  group.appendChild(cube);
  grid.translateLocal(0, 100, 0);
  group.setPosition(300, 250, 0); // add a cube to canvas

  canvas.appendChild(group); // stats

  var stats = new _stats.default();
  stats.showPanel(0);
  var $stats = stats.dom;
  $stats.style.position = 'absolute';
  $stats.style.left = '0px';
  $stats.style.top = '0px';
  var $wrapper = document.getElementById('container');
  $wrapper.appendChild($stats);
