  attrs: {
    x: 200,
    y: 200,
    r: 60,
    fill: '#1890FF',
    stroke: '#F04864',
    lineWidth: 4,
    offsetPath: offsetPathPolyline,
  },
});
const circle3 = new Circle({
  attrs: {
    x: 200,
    y: 300,
    r: 60,
    fill: '#1890FF',
    stroke: '#F04864',
    lineWidth: 4,
    offsetPath: offsetPathPath,
  },
});

canvas.appendChild(circle1);
canvas.appendChild(circle2);
canvas.appendChild(circle3);

circle1.animate(
  [
    { offsetDistance: 0 },
    { offsetDistance: 1 },
  ], {
  duration: 2500,
  easing: 'cubic-bezier(0.250, 0.460, 0.450, 0.940)',
  iterations: Infinity,
  direction: 'alternate',
});
circle2.animate(
  [
    { offsetDistance: 0 },
    { offsetDistance: 1 },
  ], {
  duration: 1500,
  easing: 'cubic-bezier(0.250, 0.460, 0.450, 0.940)',
  iterations: Infinity,
  direction: 'alternate',
});
circle3.animate(
  [
    { offsetDistance: 0 },
    { offsetDistance: 1 },
  ], {
  duration: 4500,
  easing: 'cubic-bezier(0.250, 0.460, 0.450, 0.940)',
  iterations: Infinity,
  direction: 'alternate',
});

// stats
const stats = new Stats();
stats.showPanel(0);
const $stats = stats.dom;
$stats.style.position = 'absolute';
$stats.style.left = '0px';
$stats.style.top = '0px';
const $wrapper = document.getElementById('container');
$wrapper.appendChild($stats);
canvas.on('afterRender', () => {
  if (stats) {
    stats.update();
  }
});

// GUI
